		,#{DEPT_CD}
		,#{GRADE_CD}
		,#{EMP_TEL}
		,#{EMP_DTEL}
		,#{EMP_EMAIL}
		,#{EMP_PASSWD}
		,#{EMP_POST_NO}
		,#{EMP_ADDR}
		,#{HIRE_DATE}
		,#{WORK_CD}
		,#{PRIV_CD}
		,#{PHOTO}
	)

mapper.select:
SELECT EMP_PASSWD
	FROM emp
		WHERE EMP_EMAIL = #{EMP_EMAIL}

SELECT WORK_CD
	FROM emp
		WHERE EMP_EMAIL = #{EMP_EMAIL}

SELECT EMP_NAME, PRIV_CD, IDX, EMP_NUM
	FROM emp
		WHERE EMP_EMAIL = #{EMP_EMAIL}

SELECT idx
	FROM emp
		WHERE EMP_EMAIL = #{check_email}

		SELECT *
      FROM emp e, dep p 
			WHERE e.DEPT_CD = p.DEPT_CD AND e.EMP_NAME LIKE '%${keyword}%' AND e.WORK_CD = 'C1';

SELECT * FROM emp
WHERE EMP_NAME LIKE '%{keyword}%'
LIMIT #{startRow} , #{listLimit}

SELECT DEPT_NAME
	FROM dep
	WHERE DEPT_CD=#{DEPT_CD}

SELECT GRADE_NAME
	FROM grade
	WHERE GRADE_CD=#{GRADE_CD}

SELECT PRIV_CD
	FROM emp
	WHERE EMP_NAME=#{EMP_NAME}

mapper.update:
	UPDATE emp
		SET EMP_NUM = #{EMP_NUM}
			,EMP_NAME = #{EMP_NAME}
			,DEPT_CD = #{DEPT_CD}
			,GRADE_CD = #{GRADE_CD}
			,EMP_TEL = #{EMP_TEL}
			,EMP_DTEL = #{EMP_DTEL}
			,EMP_EMAIL = #{EMP_EMAIL}



  			,EMP_POST_NO = #{EMP_POST_NO}
  			,EMP_ADDR = #{EMP_ADDR}
  			,WORK_CD = #{WORK_CD}
  			,PRIV_CD = #{PRIV_CD}

				,PHOTO = #{PHOTO}	

  		WHERE 
  			idx = #{IDX}

