photo = #{emp.PHOTO}
WHERE EMP_NUM = #{emp_num}

UPDATE emp
	SET PHOTO = REPLACE(PHOTO, #{PHOTO}, "")
	WHERE EMP_NUM = #{EMP_NUM}
